		const proc = 70;
		const opacity = 0.7;
		var pos = 0;
		var click = true;
		var width, width_w = window.innerWidth;
		var width_h = window.innerHeight;
		if (width_w > width_h)
			width = proc * width_h / 100;
		else
			width = proc * width_w / 100;
		//$('.content').css({'height' : width_h - 40 - 30});
		var TouchMenu = TouchMenuLA({
			target: document.getElementById('menu'),
			width: width,
			maxMaskOpacity: opacity,
			onOpen: function(){
				//запрещаем скролить окно
				$('body').css({'overflow' : 'hidden'});
				//animation visible ukz
				$('.circle .block').addClass('animation');
			},
			onClose: function(){
				//скролить окно
				$('body').css({'overflow-y' : 'scroll'});
				//animation visible ukz
				$('.circle .block').removeClass('animation');
			}
		});
		document.getElementById('menu-open').addEventListener('click', function(){
			TouchMenu.toggle();
		});
		$.fn.touchScrolling = function(){
    		var startPos = 0;
        	self = $(this);
        	self.bind('touchstart', function(e) {
        		startPos = self.scrollTop() + e.touches[0].pageY;
        		e.preventDefault();
        	});
		    self.bind('touchmove', function(e) {
		        self.scrollTop(startPos - e.touches[0].pageY);
		        e.preventDefault();

		    });
		};
		//hover menu
		$('.color .menu li').bind('touchend', function(e) {
			if (click)
			{
				$('.color .menu li a').each(function(index){
					$(this).removeClass('active');
				});
				var href = $(this).find('a')
					.addClass('active')
					.attr('href');
				//console.log(href);
				window.location.href = href;
				TouchMenu.close();
			}
		});
		$('.color .menu').touchScrolling();
		$('.touch-menu-la').swipe({
			swipeStatus:function(event, phase, direction, distance, duration, fingers, fingerData, currentDirection){
				if (currentDirection == 'up' || currentDirection == 'down'){
					//console.log('scroll');
					click = false;
					var s = '', str = $(this).css('transform');
					for(var i = str.length - 1; i >= 0; i--){
						if (str[i] == ',')
							{
								for(var j = i - 1; j >= 0; j--){
									if (str[j] != ',')
										s += str[j];
									else
										break;
								}
								var _s = '';
								for(var k = s.length - 1; k >= 0; k--){
									_s += s[k];
								}
								s = _s;
								break;
							}
					}
					_width_scroll = width - 20;
					if (s < _width_scroll)
					{
						var startPos = 0;
			        	self = $('.color .menu');
						self.unbind();
			        	self.bind('touchstart', function(e) {
			        		startPos = self.scrollTop() + e.touches[0].pageY;
			        		e.preventDefault();
			        	});
					    self.bind('touchmove', function(e) {
					        //self.scrollTop(startPos - e.touches[0].pageY);
					        self.scrollTop(pos);
					        e.preventDefault();
					    });
					    self.bind('touchend', function(e) {
					        self.scrollTop(pos);
					        self.unbind();
					        self.bind('touchstart', function(e) {
				        		startPos = self.scrollTop() + e.touches[0].pageY;
				        		e.preventDefault();
				        	});
						    self.bind('touchmove', function(e) {
						        self.scrollTop(startPos - e.touches[0].pageY);
						        e.preventDefault();
						    });
					        e.preventDefault();
					    });
					    self.bind('touchcancel', function(e) {
					        self.scrollTop(pos);
					        self.unbind();
					        self.bind('touchstart', function(e) {
				        		startPos = self.scrollTop() + e.touches[0].pageY;
				        		e.preventDefault();
				        	});
						    self.bind('touchmove', function(e) {
						        self.scrollTop(startPos - e.touches[0].pageY);
						        e.preventDefault();
						    });
					        e.preventDefault();
					    });
					}
					else
						pos = $('.color .menu').scrollTop();
				}
				if (phase == 'end' || phase == 'cancel')
					click = true;
			},
			threshold:0
		});
		window.addEventListener("resize", function(){
			//orientation landscape and portrait
			width, width_w = window.innerWidth;
			width_h = window.innerHeight;
			if (width_w > width_h)
				width = proc * width_h / 100;
			else
				width = proc * width_w / 100;
			//$('.content').css({'height' : width_h - 40 - 30});
		}, false);